const d_hour=24,h_min=60,m_sec=60;var m_days=[31,28,31,30,31,30,31,31,30,31,30,31];function get_time(){var g=new Date;var f=g.getMonth();var b=g.getDate();var a=g.getHours();var e=g.getMinutes();var d=g.getSeconds();var h=0;for(var c=0;c<f;++c){h+=m_days[c]}return h*d_hour*h_min*m_sec+b*d_hour*h_min*m_sec+a*h_min*m_sec+e*m_sec+d}function countdown(){var g=9;var b=1;var d=0;var a=0;var f=0;var h=0;var j;for(var e=0;e<g-1;++e){h+=m_days[e]}var c=h*d_hour*h_min*m_sec+b*d_hour*h_min*m_sec+d*h_min*m_sec+a*m_sec+f;var k=c-get_time();time.deadline=k;j=((k/(d_hour*h_min*m_sec))*10)%10;time.days=k/(d_hour*h_min*m_sec)-j/10;k-=time.days*d_hour*h_min*m_sec;j=((k/(h_min*m_sec))*10)%10;time.hour=k/(h_min*m_sec)-j/10;k-=time.hour*h_min*m_sec;j=((k/(m_sec))*10)%10;time.mins=(k/(m_sec))-j/10;k-=time.mins*m_sec;time.sec=k;return time}function start(){var c=countdown.deadline;var i=document.getElementById("countdowndays"),g=document.getElementById("countdownhours"),b=document.getElementById("countdownminutes"),f=document.getElementById("countdownseconds");counter(i,g,b,f);var e=setInterval(function(){counter(i,g,b,f)},1000);if(c===0){clearInterval(e)}}function counter(f,c,a,b){var e=countdown();f.innerHTML=Math.round(e.days)+"<br>Days";c.innerHTML=Math.round(e.hour)+"<br>Hours";a.innerHTML=Math.round(e.mins)+"<br>Min";b.innerHTML=Math.round(e.sec)+"<br>Sec"};