var canvas=document.getElementById("canvas"),ctx=canvas.getContext("2d");function resize(){canvas.width=window.innerWidth;canvas.height=window.innerHeight}resize();window.onresize=resize;function noise(c){var b=c.canvas.width,e=c.canvas.height,g=c.createImageData(b,e),f=new Uint32Array(g.data.buffer),a=f.length,d=0;for(;d<a;){f[d++]=((255*Math.random())|0)<<24}c.putImageData(g,0,0)}var toggle=true;(function loop(){toggle=!toggle;if(toggle){requestAnimationFrame(loop);return}noise(ctx);requestAnimationFrame(loop)})();